using System;
using System.Text;
using System.Diagnostics;
using System.Linq;

namespace COFFLoader
{
    public class COFFLoader
    {
        private static string beaconOutputData;

        public static int Main(string[] args)
        {
            if (args.Length < 1 || args[0] == "-h" || args[0] == "/?" || args[0] == "/h" || args[0] == "--help" || args[0] == "-help")
            {
                Console.WriteLine("Coffloader - Load and execute object files");
                Console.WriteLine("Usage      - Coffloader <object_file> <arguments>");
                Console.WriteLine("Note       - Entrypoint for object file is \"go\"");
                return 0;
            }
            const string function = "go";
            string object_file = args[0];
            string bof_args = "AAAAAA==";
            if(args.Length > 1)
            {
                bof_args = args[1];
            }
            string output = RunCoff(function, object_file, bof_args);
            Console.WriteLine(output);
            return 0;
        }

        public static string RunCoff(string functionName, string coffData, string argData)
        {
            try
            {
                Debug.WriteLine("Calling COFFLoader.RunCoff()");

                byte[] functionname = Encoding.ASCII.GetBytes(functionName);
                byte[] coff_data = Decode(coffData);
                byte[] arg_data = Decode(argData);
                string data = new string("".ToCharArray().Reverse().ToArray());
                byte[] beacon_data = Decode(data);
                // Call coffloader once with beacon_compatibility.o before loading BOF to initialize Beacon* functions
                if (
                    CoffParser.parseCOFF(new byte[] { }, beacon_data, beacon_data.Length, null, 0)
                    == 1
                )
                {
                    CoffParser.CleanUpMemoryAllocations();
                    return "parseCOFF failed: 1";
                }

                if (
                    CoffParser.parseCOFF(
                        functionname,
                        coff_data,
                        coff_data.Length,
                        arg_data,
                        arg_data.Length
                    ) == 1
                )
                {
                    beaconOutputData = "parseCOFF failed: 1";
                }
                else
                {
                    beaconOutputData = CoffParser.getBeaconOutputData();
                }
            }
            catch (Exception e)
            {
                Debug.WriteLine(string.Format("Exception: '{0}'", e));
            }

            return BeaconGetOutputData();
        }

        public static string BeaconGetOutputData()
        {
            return beaconOutputData;
        }

        public static byte[] Decode(string encodedBuffer)
        {
            return Convert.FromBase64String(encodedBuffer);
        }
    }
}
